class Point:
    def __init__(self, x, y):
        self.x = x
        self.y = y


class Rectangle:
    def __init__(self, width, height):
        self.width = width
        self.height = height


class Circle:
    def __init__(self, radius):
        self.radius = radius


class Triangle:
    def __init__(self, base, height):
        self.base = base
        self.height = height


class Square:
    def __init__(self, side):
        self.side = side
