import re
import pytest

def test_foo():
    ### Errors

    with pytest.raises(BareString, match="foobar"): ...

    with pytest.raises(MultipleParts, match="foo"
                                            "bar"): ...


    ### No errors

    with pytest.raises(NoMatch): ...

    with pytest.raises(NonLiteral, match=pattern): ...

    with pytest.raises(FunctionCall, match=frobnicate("qux")): ...

    with pytest.raises(ReEscaped, match=re.escape("foobar")): ...

    with pytest.raises(RawString, match=r"foobar"): ...

    with pytest.raises(RawStringPart, match=r"foo" 'bar'): ...

    with pytest.raises(Metacharacters, match="foo\\bar"): ...
